<?php/** * 'pwpc_bws_btn' Shortcode *  * @package PowerPack Lite * @subpackage Buttons with Style * @since 1.0 */// Exit if accessed directlyif ( !defined( 'ABSPATH' ) ) exit;function pwpcl_bws_simple_btn( $atts, $content = null ) {		// Shortcode Parameter	extract(shortcode_atts(array(		'id' => 0	), $atts, 'pwpc_bws_btn'));	// If id is not there then return	if( empty($id) ) {		return $content;	}	// Taking some variables	$prefix = PWPCL_BWS_META_PREFIX; // Metabox prefix	// Getting post data	$post_data = get_post($id);	// If it is button post type and post is publish	if(isset($post_data->post_type) && $post_data->post_type == PWPCL_BWS_POST_TYPE && $post_data->post_status == 'publish') {				ob_start();		$post_meta 	= pwpcl_bws_btn_post_sett($id);		$target 	= $post_meta['button_link_target'];		$btn_name 			= $post_meta['button_name'];		$link 				= !empty($post_meta['button_link']) ? $post_meta['button_link'] : 'javascript:void(0);';					$button_main_class 	= "pwpc-bws-btn pwpc-bws-btn-simple pwpc-bws-{$post_meta['button_class']} pwpc-bws-{$post_meta['button_style_type']} pwpc-bws-{$post_meta['button_style']} pwpc-bws-{$post_meta['button_size']} {$post_meta['extra_cls']}";		if( empty($btn_name) ) {			return $content;		}		include( PWPCL_BWS_DIR .'/templates/simple-btn.php' );		$content .= ob_get_clean();	}	return $content;}// 'pwpc_bws_btn' shortcodeadd_shortcode('pwpc_bws_btn', 'pwpcl_bws_simple_btn');